mapper.select:
SELECT
id, category_id, category_name, pic_url, book_name, author_id,
author_name, word_count, visit_count, collect_count, last_chapter_name,score,last_chapter_update_time
FROM book_info
WHERE word_count > 0

    AND (book_name LIKE concat('%', #{dto.keyword}, '%') OR
    author_name LIKE concat('%', #{dto.keyword}, '%') OR
    last_chapter_name LIKE concat('%', #{dto.keyword}, '%'))


    AND work_direction = #{dto.workDirection}


    AND category_id = #{dto.categoryId}


    AND book_status = #{dto.bookStatus}


    AND word_count >= #{dto.wordCountMin}


    AND word_count  <  #{dto.wordCountMax}


    AND last_chapter_update_time >= #{dto.updateTimeMin}


    ORDER BY ${dto.sort} DESC
